import httpClient from "../http-common";

const getAll = () => {
    return httpClient.get('/seealljobs');
}

const create = data => {
    return httpClient.post("/postjob", data);
}

const update = data => {
    return httpClient.put('/updatejob', data);
}

const remove = id => {
    return httpClient.delete(`/deletejob/${id}`);
}
export default { getAll, create, update, remove };
